package corejava;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1,n2;
		n1=30;
		n2=20;
		System.out.println("number 1+number 2: "+(n1+n2));
		System.out.println("number 1-number 2: "+ (n1-n2));
		System.out.println("number 1*number 2: "+(n1*n2));
		System.out.println("number 1/number 2: "+(n1/n2));
		System.out.println("number 1%number 2: "+(n1%n2));

	}

}
